import React from 'react';
import quote_data from '../assets/quotes.json'
import '../stylesheets/Home.css';
import RandomImage from '../components/RandomImage'

function Home () {
    let id = 0; /* change to a verifiable randomizer hash based on the date up to hour*/
    let quote = quote_data['quotes'][id]

    return (

      <div className="homepage">
        <div className="ImageContainer">
          <RandomImage/>
        </div>
        <div className="quote-body" dangerouslySetInnerHTML={{ __html: quote.text }}></div>
        <div className="quote-prompt" dangerouslySetInnerHTML={{ __html: quote.prompt }}></div>
      </div>
    )
}

export default Home
