import React, { useState, useEffect } from 'react';

function RandomImage() {
  const [imageSrc, setImageSrc] = useState(null);
  const [images, setImages] = useState([]);

  useEffect(() => {
    // Dynamically import all image files from the 'img' directory
    import.meta.glob('/img/ai/*.{jpg,jpeg,png,gif,svg}').then((imageFiles) => {
      const imageNames = Object.keys(imageFiles).map((key) => {
        // Extract the image file name from the file path
        return key.replace('/img/', '');
      });
      setImages(imageNames);
    });
  }, []);

  useEffect(() => {
    if (images.length > 0) {
      // Select a random image from the available images
      const randomIndex = Math.floor(Math.random() * images.length);
      const randomImageName = images[randomIndex];
      setImageSrc(`/img/ai/${randomImageName}`);
    }
  }, [images]);

  return (
    <div>
      {imageSrc && <img src={process.env.PUBLIC_URL + imageSrc} alt="Random Image" />}
    </div>
  );
}

export default RandomImage;
