import React from 'react';
import { useParams } from "react-router";
import quote_data from '../assets/quotes.json'
import '../stylesheets/Home.css';

function Home () {
    let { id } = useParams(); /* change to a verifiable randomizer hash based on the date up to hour*/
    let article = quote_data['quotes'][id]

    return (
        <div className="article" key={article.id}>
          <h2 className="article-title particular-article-title">{article.title}</h2>
          <p className="article-meta">{article.date}</p>
          <div className="article-body" dangerouslySetInnerHTML={{ __html: article.body }}></div>
        </div>
    )
}



export default Home
