import React, { useState, useEffect } from 'react';
import '../stylesheets/Home.css';
import RandomImage from '../components/RandomImage';
import RandomQuote from '../components/RandomQuote';
import Hangman from '../components/Hangman';


function Home () {
  const [prompt, setPrompt] = useState('');

  const handlePromptUpdate = (newPrompt) => {
      setPrompt(newPrompt);
  }

  return (

    <div className="homepage">
      <div className='title-container'>
        <h1 className='title'>Guess the Prompt</h1>
      </div>
      <div className='randomimageai'>
        <RandomImage onPromptUpdate={handlePromptUpdate}/>
      </div>
      <div className='hangman-parent-container'>
        <Hangman word={prompt || "hello world  comedy"}/>
      </div>
      <div className="quote-wrapper">
        <RandomQuote onPromptUpdate={handlePromptUpdate}/>
      </div>
      <div className='hangman-parent-container'>
        <Hangman word={prompt || "hello world  comedy"}/>
      </div>
      <p className='endmessage'>check back each hour for a new challenge</p>
    </div>
  )
}

export default Home;
