import React, { useState, useEffect } from 'react';
import quoteData from '../assets/imageprompts.json';
import getHourlyPseudoRandomIdx from './helpers';

function RandomImage() {
  const [imageSrc, setImageSrc] = useState(null);
  const [images, setImages] = useState([]);

  // Get the image file names when the component mounts
  useEffect(() => {
    const context = require.context("../assets/img/ai", false, /\.(png|jpe?g|gif|svg)$/);
    const imageNames = context.keys().map((key) => key.replace(/^.*[\\/]/, ''));
    setImages(imageNames);
  }, []);

  // Set the image source when the component mounts or when the images array changes
  useEffect(() => {
    console.log(images)
    if (images.length > 0) {
      const randomImageidx = getHourlyPseudoRandomIdx(images.length);
      const randomImageSrc = require(`../assets/img/ai/${images[randomImageidx]}`)
      setImageSrc(randomImageSrc);
    }
  }, [images]);

  return ( 
    <div>
      {imageSrc && <img src={imageSrc} alt="Random Image" />}
    </div>
  );
}

export default RandomImage;
