import React, { useState, useEffect } from 'react';

function RandomImage() {
  const [imageSrc, setImageSrc] = useState(null);
  const [images, setImages] = useState([]);

  // Function to randomly select an image from the array
  const getRandomImage = () => {
    const randomIndex = Math.floor(Math.random() * images.length);
    const randomImage = images[randomIndex];
    return `src/img/${randomImage}`;
  };

  // Get the image file names when the component mounts
  useEffect(() => {
    const context = require.context('../assets/img', false, /\.(png|jpe?g|gif|svg)$/);
    const imageNames = context.keys().map((key) => key.replace(/^.*[\\/]/, ''));
    setImages(imageNames);
  }, []);

  // Set the image source when the component mounts or when the images array changes
  useEffect(() => {
    if (images.length > 0) {
      const randomImageSrc = getRandomImage();
      setImageSrc(randomImageSrc);
    }
  }, [images]);

  return (
    <div>
      {imageSrc && <img src={imageSrc} alt="Random Image" />}
    </div>
  );
}

export default RandomImage;
